// Post.js
import React from 'react';

const Post = ({ post }) => {
  if (!post) {
    
    return <div>Loading...</div>;
  }

  return (
    <div className="post">
      <h1>{post.title}</h1>
      <p className="postDate">{post.datetime}</p>
      <p className="postBody">
        {
          (post.body).length <= 25 ? post.body : `${(post.body).slice(0, 25)}...`}
      </p>
    </div>
  );
};

export default Post;
