import React from 'react'
import { FaLaptop, FaMobileAlt, FaTabletAlt } from 'react-icons/fa'

const Header = ({title}) => {
  const {width} = window.innerWidth
  return (
    <header className='Header'>
      <h1>{title}</h1>
      {width < 768 ? <FaMobileAlt/> 
      : width < 992 ? <FaTabletAlt/>
      :  <FaLaptop/>
      }
     
      </header>
  )
}

export default Header
