import About from "./components/About";
import Header from "./components/Header";
import Home from "./components/Home";
import Missing from "./components/Missing";
import Nav from "./components/Nav";
import NewPost from "./components/NewPost";
import PostPage from "./components/PostPage";
import Post from "./components/Post";
// import PostLayout from "./components/PostLayout";
import Footer from "./components/Footer";
import { useNavigate } from 'react-router-dom';
import { useState } from "react";
import { format } from 'date-fns';

function App() {

  const [posts, setPosts] = useState([
    {
      id: 1,
      title: "My Post 1",
      datetime: "July 07, 2021",
      body: "quia et suscipit\nsuscipit recusandae consequuntur expedita et cum\nreprehenderit molestiae ut ut quas totam\nnostrum rerum est autem sunt rem eveniet architecto",
    },
    {
      id: 2,
      title: "My Post 2",
      datetime: "October 07, 2021",
      body: "est rerum tempore vitae\nsequi sint nihil reprehenderit dolor beatae ea dolores neque\nfugiat blanditiis voluptate porro vel nihil molestiae ut reiciendis\nqui aperiam non debitis possimus qui neque nisi nulla",
    },
    {
      id: 3,
      title: "My Post 3",
      datetime: "September 07, 2021",
      body: "et iusto sed quo iure\nvoluptatem occaecati omnis eligendi aut ad\nvoluptatem doloribus vel accusantium quis pariatur\nmolestiae porro eius odio et labore et velit aut",
    },
    {
      id: 4,
      title: "My Post 4",
      datetime: "May 07, 2021",
      body: "ullam et saepe reiciendis voluptatem adipisci\nsit amet autem assumenda provident rerum culpa\nquis hic commodi nesciunt rem tenetur doloremque ipsam iure\nquis sunt voluptatem rerum illo velit",
    },
    {
      id: 5,
      title: "nesciunt quas odio",
      datetime: "March 28, 2021",
      body: "repudiandae veniam quaerat sunt sed\nalias aut fugiat sit autem sed est\nvoluptatem omnis possimus esse voluptatibus quis\nest aut tenetur dolor neque",
    },
  ]);

  const [search, setSearch] = useState("");
  const [searchResults, setsearchResults] = useState([]);
  const [postTitle, setPostTitle] = useState('');
  const [postBody, setPostBody] = useState('');

  useEffect (() => {
    const filteredResult = posts.filter((post) => ((post.body).toLowerCase()).includes(search.to))
  })

  const handleSubmit = (e) => {
    e.preventDefault();
    const id = posts.length ? posts[posts.length - 1].id + 1 : 1 ;
    const datetime = format(new Date(), ' yyyy pp ,MM-dd');
    const newPost = {id, title : postTitle, datetime, body: postBody};
    const allPosts = [...posts, newPost];
    setPosts(allPosts);
    setPostTitle('');
    setPostBody('');
    // navigate('/');
  }

  return (
    <div className="App">

      <Header title="Karthik header" />
      <Nav 
      search={search} 
      setSearch={setSearch} />
      <Home posts={posts} />
      <NewPost />
      {/* <Post /> */}
      <PostPage />
      <About />
      <Missing />
      <Footer />
    </div>
  );
}

export default App;
