// Post.js
import React from 'react';
import { useParams } from 'react-router-dom';

const Post = ({post}) => {
  const { id } = useParams();  // id comes from the URL params
  
  return (
    <div className=''>
      <h1>Post Page</h1>
      <h1>
        Post {id}
      </h1>
    </div>
  );
};

export default Post;
